import React from "react";
import FoodList from "./FoodList";
import "./FoodCard.scss";
import Container from "react-bootstrap/Container";
import "bootstrap/dist/css/bootstrap.min.css";
import DashMenu from './Components/DashMenu'
class FoodCard extends React.Component {
  constructor() {
    super();
    this.state = {
      fooditems: FoodList
    };
  }
  render() {
    const fudList = this.state.fooditems.map(item => (
      <Container>
                 <div key={item.key} className="food-card">
            <img src={item.imageUrl} className="image" alt="food" />

            <div className="food-info">
              <h5 className="food-name">{item.name}</h5>
              <p className="food-desc"> {item.description} </p>
            </div>

            <div className="food--price">
              <p className="food--price__order"> {item.order} </p>
              <button className=" food--price__btn btn-action">
                Add to Cart
              </button>
            </div>
          </div>
      
      </Container>
    ));

    return (
      <div className="food-card-wrapper">
       <DashMenu/>
          <input
            type="text"
            id="filter"
            placeholder="Find Food"
            ref={input => (this.search = input)}
            onChange={this.handleInputChange}
          />
       <p>Please note that you can individually add item to cart or you can just add all item to cart below</p>
       <h5>Local Assorted</h5>
      <div>{fudList}</div>
     </div> 
    );
  }
}

export default FoodCard;
