import React from "react";
import "../Navigation/Navigation.scss";
import {Button} from 'react-bootstrap';
import {useState} from 'react-bootstrap';
import {useEffect} from 'react-bootstrap';
import {render} from 'react-bppot'



function simulateNetworkRequest() {
  return new Promise(resolve => setTimeout(resolve, 2000));
}

function NavLoggedin() {
  const [isLogin, setLoading] = useState(false);

  useEffect(() => {
    if (isLogin) {
      simulateNetworkRequest().then(() => {
        setLoading(false);
      });
    }
  }, [isLogin]);

  const handleClick = () => setLoading(true);

  return (
    <Button
      variant="primary"
      disabled={isLogin}
      onClick={!isLogin ? handleClick : null}
    >
      {isLogin ? 'LOGIN/SIGNUP' : 'Click to load'}
    </Button>
  );
}

render(<NavLoggedin />);


export default NavLoggedin;
