import React from "react";
import FoodList from "./FoodList";
import "./FoodCard.scss";
import Container from 'react-bootstrap/Container'
import 'bootstrap/dist/css/bootstrap.min.css';
class FoodCard extends React.Component {
  constructor() {
    super();
    this.state = {
      fooditems: FoodList
    };
  }
  render() {
    const fudList = this.state.fooditems.map(item => (
      
      <Container>
        
        <div className="food-card-wrapper">
        <div key={item.key} className="food-card">
        <img src={item.imageUrl} className="image" alt="food" />

        <div className="food-info">
          <h5>{item.name}</h5>
          <p> {item.description} </p>
        </div>

        <div className="food--price">
          <p className="food--price__order"> {item.order} </p>
          <button className=" food--price__btn btn-action">Add to Cart</button>
        </div>
      </div>
        </div>
        
      </Container>
      
    ));

    return (
      <div>
         <form>
                    <input type="text" id="filter" placeholder="Search for..." ref={input => this.search = input} onChange={this.handleInputChange}/>
                </form>
        <div>{fudList}</div>
      </div>
    );
  }
}

export default FoodCard;
